import React, { Component } from 'react';
import Recipe from "./Recipe"
import './App.css';


class App extends Component {
  constructor() {
    super();
    this.state = {
      recipes: []
    }
    this.somefunc = this.somefunc.bind(this)
  }
  componentDidMount(){
    var self = this;

    var recipe1 = {
      name: "Test10",
      description: "something longer",
      link: "http://localhost:3000/Interview-App-React",
      imgLink: "https://food.fnr.sndimg.com/content/dam/images/food/fullset/2020/03/10/0/FNK_BEST-CHICKEN-AND-RICE-H_s4x3.jpg.rend.hgtvcom.966.725.suffix/1583851621211.jpeg",
      recommend: true
    };
    var recipe2 = {
      name: "Test2",
      description: "something else longer ",
      link: "https://www.google.com/?client=safari",
      recommend: false
    };
    var recipe3 = {
      name: "Test3",
      description: "something else 1231231 ",
      link: "https://www.google.com/?client=safari",
      recommend: false
    };

    var recipeArray = [];
    recipeArray.push(recipe1, recipe2, recipe3)
    self.setState({
      recipes: recipeArray
    });
  }
  somefunc(){
  }
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <section id = "Recipes">
            {this.state.recipes.map((newRecipe)=>
              <Recipe
                key = {newRecipe.name.toString()}
                name = {newRecipe.name}
                description = {newRecipe.description}
                link = {newRecipe.link}
                recommend = {newRecipe.recommend}
                somefunc = {this.somefunc}
              />
            )}

          </section>
        </header>
      </div>
    );
  }
}
export default App;
