import React, { Component } from 'react';
import Recipe from "./Recipe"
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      recipes: []
    }
    this.somefunc = this.somefunc.bind(this)
  }
  componentDidMount(){
    var self = this;

    var recipe1 = {
      name: "Test1",
      description: "something longer",
      link: "http://localhost:3000/Interview-App-React",
      recommend: true
    };
    var recipe2 = {
      name: "Test2",
      description: "something else longer ",
      link: "https://www.google.com/?client=safari",
      recommend: false
    };
    var recipe3 = {
      name: "Test3",
      description: "something else 1231231 ",
      link: "https://www.google.com/?client=safari",
      recommend: false
    };

    var recipeArray = [];
    recipeArray.push(recipe1, recipe2, recipe3)
    self.setState({
      recipes: recipeArray
    });
  }
  somefunc(){
  }
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <section id = "Recipes">
            {this.state.recipes.map((newRecipe)=>
              <Recipe
              somefunc = {this.somefunc} 
            />
            )}

          </section>
        </header>
      </div>
    );
  }
}
export default App;
