import React from 'react';
import {Component} from 'react';
import Recipe from "./Recipe"
import './App.css';


class App extends Component {
  constructor() {
    super();
    this.state = {
      recipes: []
    }
    this.somefunc = this.somefunc.bind(this)
  }
  componentDidMount(){
    var self = this;

    var recipe1 = {
      name: "Discord Elo Bot",
      description: "A discord bot aimed at tracking 'ratings' or 'elo' in online seasonal competitions. This is a passion project that started as a discussion between users on a Magic the Gathering(MTG) Discord Server. With the recent quarantine, people are unable to leave their homes to play MTG at their local card stores. With this, the MTG community has taken the online Discord Servers by storm. They have created competitive enviornments where users play online, and then 'log' them with the help of a Discord Bot. Wins and losses raise and lower your online 'score' respectively. Many issues were discovered with the current bot used on this server. These include and not limited to: restrictions, direct contact to the developer and user experiences being ignored. From this, a core group of designers, led by myself were assembled tocreate a new bot. With closed beta expected September 1st, we are aiming to expand to the greater magic community soon after. While in the development stage, the bot is being worked on by a few developers led by myself. As we reach our initial release, it will become an open sourced project with the hopes of including the community.",
      date: "August 16th, 2020",
      links:
      [
        "https://noahsaldanadesign.com/img/Discord%20Bot.png",
        "https://noahsaldanadesign.com/img/Discord%20Bot2.png",
        "https://noahsaldanadesign.com/img/Discord%20Bot3.png",
      ],
      recommend: true
    };
    var recipe2 = {
      name: "Aliquam in varius dui, in.",
      description: "Vestibulum maximus convallis ante at accumsan. Aenean pretium lacus id viverra feugiat. Nulla sit amet interdum nunc, finibus faucibus risus. Cras aliquam convallis purus ac rhoncus. Fusce rutrum ex quis aliquet pulvinar. Nulla nec pellentesque lacus, accumsan pulvinar odio. ",
      date: "August 10th, 2020",
      links:
      {
        link1: "https://noahsaldanadesign.com/img/Discord%20Bot.png",
        link2: "https://noahsaldanadesign.com/img/Discord%20Bot2.png",
        link3: "https://noahsaldanadesign.com/img/Discord%20Bot3.png",
      },
      recommend: false
    };
    var recipe3 = {
      name: "Praesent id pharetra purus. Proin.",
      description: "Sed ullamcorper id purus nec lobortis. Mauris pharetra porttitor purus vel interdum. Aliquam condimentum libero pretium erat eleifend, ac egestas tortor molestie. Aliquam venenatis turpis leo, vitae euismod tellus mollis at. Quisque congue dolor mauris. Ut accumsan nec felis sed finibus. Phasellus magna magna, vulputate at nisi sit amet, dapibus blandit ex. Curabitur ut tortor eu tortor convallis suscipit. Pellentesque felis magna, aliquam a lacus quis, hendrerit blandit nisl. Vivamus id magna eros. Aenean vitae fermentum dolor, rhoncus consectetur nisi.",
      date: "August 1st, 2020",
      links:
      {
        link1: "https://noahsaldanadesign.com/img/Discord%20Bot.png",
        link2: "https://noahsaldanadesign.com/img/Discord%20Bot2.png",
        link3: "https://noahsaldanadesign.com/img/Discord%20Bot3.png",
      },
      recommend: false
    };

    var recipeArray = [];
    recipeArray.push(recipe1, recipe2, recipe3)
    self.setState({
      recipes: recipeArray
    });
  }
  somefunc(){
  }
  render(){
    return (
        <div className="App">
          <header className="App-header">
            <section className = "Welcome-text">
              <h1><strong>Hey, I'm Noah Salda√±a</strong></h1>
                <section className = "Welcome-sub">
                  <p>iOS Developer - Student Mentor - Human-Centered Designer</p>
                </section>
            </section>
            <section className = "Page-nav">
            {/* <Button
              onPress={console.log("test")}
              title="Learn More"
              color="#841584"
              accessibilityLabel="Learn more about this purple button"
            /> */}
            </section>
            <section id = "Recipes">
              {this.state.recipes.map((newRecipe)=>
                <Recipe
                  key = {newRecipe.name.toString()}
                  name = {newRecipe.name}
                  date = {newRecipe.date}
                  description = {newRecipe.description}
                  links = {newRecipe.links}
                  recommend = {newRecipe.recommend}
                  somefunc = {this.somefunc}
                />
              )}
            </section>
          </header>
        </div>
    );
  }
}
export default App;
