import React, { Component } from 'react';
import './Pictures.css';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import FormLabel from '@material-ui/core/FormLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
  },
  gridList: {
    width: 750,
    height: 450,
    paddingLeft: 100,
  },
}));
const entries = [
  {
    name: "Discord Elo Bot",
    description: "A discord bot aimed at tracking 'ratings' or 'elo' in online seasonal competitions. This is a passion project that started as a discussion between users on a Magic the Gathering(MTG) Discord Server. With the recent quarantine, people are unable to leave their homes to play MTG at their local card stores. With this, the MTG community has taken the online Discord Servers by storm. They have created competitive enviornments where users play online, and then 'log' them with the help of a Discord Bot. Wins and losses raise and lower your online 'score' respectively. Many issues were discovered with the current bot used on this server. These include and not limited to: restrictions, direct contact to the developer and user experiences being ignored. From this, a core group of designers, led by myself were assembled tocreate a new bot. With closed beta expected September 1st, we are aiming to expand to the greater magic community soon after. While in the development stage, the bot is being worked on by a few developers led by myself. As we reach our initial release, it will become an open sourced project with the hopes of including the community.",
    date: "August 16th, 2020",
    link: "https://noahsaldanadesign.com/img/Discord%20Bot.png",
    recommend: true
  }
]

