import React, { Component } from 'react';
import './Recipe.css';

class Recipe extends Component{
    constructor(props){
        super(props);
        this.somefunc = this.somefunc.bind(this)
    }
    somefunc(event){
        event.preventDefault();
        this.props.somefunc();
    }
    render(){
        const listItems = this.props.links.map((link) =>
            <img key ={link} src={link} alt="Logo" className="Blog-image"></img>
        );
        return (
            <div className="Recipe">
                    <section className="Recipe-name">
                        {this.props.name}
                    </section>
                    {/* <section className="Recipe-image-section">
                        <img src={this.props.imgLink} alt="Logo" className="Recipe-image"/>
                    </section> */}
                    <section className="Recipe-description">
                        <div className = "Left-image">
                            {listItems}
                        </div>
                        <div className ="Right-text">
                            {this.props.description}
                        </div>

                    </section>
            </div>
        );  
    }
}
export default Recipe;
